<?php
chdir('/home/techingc/siar.teching.com.pe');

// Cargar configuración de la base de datos
require_once 'src/config/Database.php';

// Cargar EmailConfig
require_once 'EmailConfig.php';

// Instanciar modelos
require_once 'src/models/TrabajadorModel.php';
require_once 'src/models/RendicionesModel.php';
$trabajadorModel = new TrabajadorModel();
$rendicionModel = new RendicionesModel();

// Instanciar EmailConfig
$emailConfig = new EmailConfig();

// Obtener fecha actual
$fecha_actual = date('Y-m-d');

// Obtener los 20 registros más recientes
$rendiciones = $rendicionModel->getUltimasRendiciones(20);

$logoBase64 = 'data:image/png;base64,';

foreach ($rendiciones as $rendicion) {
    $fecha_rendicion = $rendicion['fecha_rendicion'];
    $id_usuario = $rendicion['id_usuario'];
    $id_rendicion = $rendicion['id'];

    // Calcular diferencia de días
    $diferencia_dias = (strtotime($fecha_rendicion) - strtotime($fecha_actual)) / (60 * 60 * 24);
    if ($diferencia_dias <= 1 && $diferencia_dias > 0) { // Faltan 1 día o menos, pero no ha pasado
        // Obtener el estado más reciente de tb_historial_rendiciones
        $estado_actual = $rendicionModel->getUltimoEstadoRendicion($id_rendicion);
        if ($estado_actual !== 'Autorizado') {
            // Obtener datos del solicitante
            $usuario = $rendicionModel->getUsuarioPorId($id_usuario);
            $dni = $usuario['dni'];
            $solicitante = $trabajadorModel->getTrabajadorByDni($dni);
            $correo_solicitante = $solicitante['correo'];

            $departamento = $solicitante['departamento'];
            $aprobadores = $trabajadorModel->getAprobadoresByDepartamento($departamento);

            // Enviar correo
            $asunto = "SIAR - TECHING - Rendición N° $id_rendicion";
            $mensaje = "<p><img alt='Logo SIAR' src='{$logoBase64}' width='140' /></p>
                    <h2>Recordatorio de Rendición</h2>
                    <p>El estado de su rendición, aún no se encuentra autorizada.</p>
                    <p>Por favor, asegúrse de haberla completado correctamente y de que sea autorizada por su jefatura a la brevedad En su estado actual, (<strong>$estado_actual</strong>), se le impedirá al área de contabilidad marcarla como finalizada.</p>
                    <hr>
                    <br>
                    <p>No es necesario responder a este mensaje. Deberá ingresar a la plataforma SIAR para obtener más detalles de la rendición.</p>
                    <a href='{$url_plataforma}'>SIAR - TECHING</a>";
            $cc = $aprobadores; // Agrega correos en copia si es necesario
            if ($emailConfig->sendSiarNotification($correo_solicitante, $asunto, $mensaje, $cc)) {
                error_log("Correo enviado correctamente a $correo_solicitante para rendición #$id_rendicion");
            } else {
                error_log("Error al enviar correo a $correo_solicitante para rendición #$id_rendicion");
            }
        }
    }
}

error_log("Se terminó con el script de notificar rendiciones");